const puppeteer = require('puppeteer');

let scraping = async () => {
  //  Creation d'une instance du navigateur chromium
  const browser = await puppeteer.launch();
  const page = await browser.newPage();
  // Navigation vers l'url cible
  await page.goto('https://www.ungm.org/Public/Notice' , {waitUntil: "networkidle2"});
  // Fonction pour scroller vers le bas
  await scrollToBottom(page);
  // Temps d'attente du resultat
  let oneMinute = 1000 * 60 * 1 // une minute
  await page.waitForTimeout(oneMinute);
  // recuperation du resultat
  let result = await page.$eval("*", (elem => elem.innerHTML))
  await browser.close();
  return {result};
};
async function scrollToBottom(page) {
  const distance = 4000; // should be less than or equal to window.innerHeight
  const delay = 4000;
  while (await page.evaluate(() => document.scrollingElement.scrollTop + window.innerHeight < document.scrollingElement.scrollHeight)) {
    await page.evaluate((y) => { document.scrollingElement.scrollBy(0, y); }, distance);
    await page.waitForTimeout(delay);
  }
}
// resultat final
let data = scraping()

return data

const puppeteer = require('puppeteer');


let scraping = async () => {
  const browser = await puppeteer.launch();
  const page = await browser.newPage();
  await page.goto('https://community.vortal.biz/public/?SkinName=conseileurope&Language=fr&AuthorityVat=77886008000010APE990Z&currentLanguage=fr' , {waitUntil: "networkidle2"});

  let items = "";
  let linkItems = "";
  let lastPageNumber = 10; // number of page
  // defined simple loop to iterate over number of catalogue pages
  for (let index = 0; index < lastPageNumber; index++) {
      // wait 40 sec for page load
      await page.waitForTimeout(40000);
      // call and wait extractedEvaluateCall and concatenate results every iteration.
      items= items + await extractedEvaluateCall(page);
      // get url of item in new tab and close new tab
      for (var i = 2; i < 12; i++)
      {
        const pageTarget = page.target();
        await page.click("div:nth-child("+ i +") > .ant-card-body a");
        const newTarget = await browser.waitForTarget(target => target.opener() === pageTarget);
        const newPage = await newTarget.page();
        let url = await newPage.evaluate(() => document.location.href);
        let separe = "<p>"+ url + "</p>";
        linkItems = linkItems + separe;
        await newPage.close()
      }
      if (index != lastPageNumber - 1) {
          // Click on next button
          await page.click('li.ant-pagination-next > a.ant-pagination-item-link');
      }
  }
  await browser.close();
  return {"items": items, "linkItems": linkItems};
};
// funtion to extract data
async function extractedEvaluateCall(page) {
  return page.evaluate(() => {
      let data = "";
      let elements = document.querySelector('*');
      data = data + elements.innerHTML;
      return data;
  });
}

// resultat final
let data = scraping()

return data

